class ChatMessage extends ActorMixin(HTMLElement) {
  readonly address = address('chat-message-' + this.id);
  private content = signal('');

  async receive(msg: Message): Promise<MessageResponse> {
    switch (msg.type) {
      case 'update':
        this.content.set(msg.payload.text);
        return { success: true };
      default:
        return { success: false, error: 'Unknown message type' };
    }
  }

  port(name: 'status' | 'interactions'): Channel<Event> {
    switch (name) {
      case 'status':
        return signalToChannel(this.statusSignal);
      case 'interactions':
        return domEventChannel(this, 'click');
    }
  }

  connectedCallback() {
    super.connectedCallback();
    actorRegistry.register(this.address, this);
    effect(() => this.render());
  }

  render() {
    this.innerHTML = html`<div>${this.content.get()}</div>`;
  }
}
